import pytest

from biomics.string.kmers import (
    kmer_frequencies,
    count_ocurrences,
    kmer_locations,
    find_clumps,
    approximate_kmer_locations,
    kmer_neighbors,
    most_frequent_approximate_kmers,
)


@pytest.mark.parametrize(
    "s, t, expected",
    [
        ("GCGCG", "GCG", 2),
        ("ACGTACGTACGT", "CG", 3),
        (
            "",
            "AGCCTTTAG",
            294,
        ),
    ],
)
def test_count_ocurrences(s, t, expected):
    assert count_ocurrences(s, t) == expected


def test_kmer_frequencies(genome):
    freqs = kmer_frequencies(genome, 4)
    assert freqs["GCAT"] == 3
    assert freqs["CATG"] == 3
    assert freqs["ATGA"] == 2
    assert freqs["TCGC"] == 1


def test_kmer_locations():
    assert kmer_locations("GATATATGCATATACTT", "ATAT") == [1, 3, 9]


def test_find_clumps():
    expected = ['GAAGA', 'CGACA']
    clumps = find_clumps(
        genome='CGGACTCGACAGATGTGAAGAACGACAATGTGAAGACTCGACACGACAGAGTGAAGAGAAGAGGAAACATTGTAA',
        k=5,
        window_size=50,
        clump_density=4,
    )
    assert sorted(clumps) == sorted(expected)


def test_approximate_kmer_locations():
    expected = [6, 7, 26, 27]
    locations = approximate_kmer_locations(
        genome='CGCCCGAATCCAGAACGCATTCCCATATTTCGGGACCACTGGCCTCCACGGTACGGACGTCAATCAAAT',
        kmer='ATTCTGGA',
        d=3,
    )
    assert sorted(locations) == sorted(expected)


def test_kmer_neighbors_non_strict():
    test_case = 'ACG'
    expected = ['CCG', 'TCG', 'GCG', 'AAG', 'ATG', 'AGG', 'ACA', 'ACC', 'ACT', 'ACG']
    assert sorted(kmer_neighbors(test_case, d=1, strict=False)) == sorted(expected)


def test_kmer_neighbors_strict():
    test_case = 'ACG'
    expected = [
        'AAA', 'CCA', 'GCA', 'TCA', 'AGA', 'ATA', 'AAC', 'CCC', 'GCC', 'TCC', 'AGC', 'ATC', 'CAG', 'GAG', 'TAG',
        'CGG', 'GGG', 'TGG', 'CTG', 'GTG', 'TTG', 'AAT', 'CCT', 'GCT', 'TCT', 'AGT', 'ATT',
    ]
    assert sorted(kmer_neighbors(test_case, d=2, strict=True)) == sorted(expected)


def test_most_frequent_approximate_kmers():
    genome = 'GGATGGTTGGCTCCCGGCCGGCGGTGGATGGTCGGCCGGCCGGCTCCTCCGGATTCCCGGCGGTGGATTGGCGGAT' \
             'GGATTGGCCGGCTCCGGATCGGCTGGCTGGCGGATTCCTCCTGGCCGGCTGGCCGGCGGTCGGCGGATGGATCGGC' \
             'GGTGGATGGATTGGCTCCTGGCGGTGGTGGATTGGCGGTCGGCGGATTGGCGGATCGGCGGTGGTTGGCCGGCGGA' \
             'TGGATCGGCTGGCGGATTGGCTCCTCCGGTGGTCGGCGGATTGGCGGATGGTGGTCGGCTGGCTGGCTGGCTGGCG' \
             'GTTCCCGGCTCCTCCCGGCGGATTGGCTCCTCCGGTCGGCCGGCTCCTCCGGATGGATTCCTGGCGGTTGGCGGTT' \
             'GGC'
    k = 5
    d = 2
    assert most_frequent_approximate_kmers(genome, k=k, d=d, strict=False) == ['GGGGG']
